    <script>
        "use strict";

        let currencyPosition = "{{ Session::get('currency_position') }}";
        let currencyIcon = "{{ Session::get('currency_icon') }}";

        function calculateMonthlyPayment(loanAmount, interestRate, loanTermYears) {
            let monthlyInterestRate = (interestRate / 100) / 12;
            let totalPayments = loanTermYears * 12;

            let monthlyPayment = loanAmount * (monthlyInterestRate * Math.pow(1 + monthlyInterestRate, totalPayments))
                / (Math.pow(1 + monthlyInterestRate, totalPayments) - 1);

            return monthlyPayment;
        }

        function calculateTotalInterest(loanAmount, interestRate, loanTermYears) {
            let totalPayments = loanTermYears * 12;
            let totalInterest = (calculateMonthlyPayment(loanAmount, interestRate, loanTermYears) * totalPayments) - loanAmount;

            return totalInterest;
        }

        function calculateTotalPayment(loanAmount, interestRate, loanTermYears) {
            let totalPayments = loanTermYears * 12;
            let totalPayment = calculateMonthlyPayment(loanAmount, interestRate, loanTermYears) * totalPayments;

            return totalPayment;
        }

        $("#calculate_btn").on("click", function (e) {
            e.preventDefault();

            let loanAmount = $("#loan_amount").val();
            let interestRate = $("#interest_rate").val();
            let loanTermYears = $("#total_year").val();

            if (!loanAmount) {
                toastr.error("{{ __('translate.Please fill out the form') }}")
                return;
            }

            if (!interestRate) {
                toastr.error("{{ __('translate.Please fill out the form') }}")
                return;
            }

            if (!loanTermYears) {
                toastr.error("{{ __('translate.Please fill out the form') }}")
                return;
            }

            let finalPayment = calculateMonthlyPayment(loanAmount, interestRate, loanTermYears)
            finalPayment = finalPayment.toFixed(2)
            finalPayment = finalPayment.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");


            let totalInterest = calculateTotalInterest(loanAmount, interestRate, loanTermYears);
            totalInterest = totalInterest.toFixed(2);
            totalInterest = totalInterest.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");

            let totalPayment = calculateTotalPayment(loanAmount, interestRate, loanTermYears);
            totalPayment = totalPayment.toFixed(2);
            totalPayment = totalPayment.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");


            let appendCurrency = '';
            let appendInterestCurrency = '';
            let appendTotalCurrency = '';

            if (currencyPosition == 'before_price') {
                appendCurrency = `${currencyIcon}${finalPayment}`
                appendInterestCurrency = `${currencyIcon}${totalInterest}`
                appendTotalCurrency = `${currencyIcon}${totalPayment}`
            } else if (currencyPosition == 'before_price_with_space') {
                appendCurrency = `${currencyIcon} ${finalPayment}`
                appendInterestCurrency = `${currencyIcon} ${totalInterest}`
                appendTotalCurrency = `${currencyIcon} ${totalPayment}`
            } else if (currencyPosition == 'after_price') {
                appendCurrency = `${finalPayment}${currencyIcon}`
                appendInterestCurrency = `${totalInterest}${currencyIcon}`
                appendTotalCurrency = `${totalPayment}${currencyIcon}`
            } else if (currencyPosition == 'after_price_with_space') {
                appendCurrency = `${finalPayment} ${currencyIcon}`
                appendInterestCurrency = `${totalInterest} ${currencyIcon}`
                appendTotalCurrency = `${totalPayment} ${currencyIcon}`
            }

            $("#monthly_payment").html(appendCurrency);
            $("#total_interest").html(appendInterestCurrency);
            $("#total_payment").html(appendTotalCurrency);
        })

        $("#loan_amount").on("keyup", function (e) {
            let enteredValue = e.target.value;
            let numericValue = enteredValue.replace(/[^0-9.]/g, '');
            $(this).val(numericValue);
        })

        $("#interest_rate").on("keyup", function (e) {
            let enteredValue = e.target.value;
            let numericValue = enteredValue.replace(/[^0-9.]/g, '');
            $(this).val(numericValue);
        })

        $("#total_year").on("keyup", function (e) {
            let enteredValue = e.target.value;
            let numericValue = enteredValue.replace(/[^0-9.]/g, '');
            $(this).val(numericValue);
        })

        $("#reset_btn").on("click", function () {
            $("#loan_amount").val('');
            $("#interest_rate").val('');
            $("#total_year").val('');

            let appendCurrency = '';

            if (currencyPosition == 'before_price') {
                appendCurrency = `${currencyIcon}0.00`
            } else if (currencyPosition == 'before_price_with_space') {
                appendCurrency = `${currencyIcon} 0.00`
            } else if (currencyPosition == 'after_price') {
                appendCurrency = `0.00${currencyIcon}`
            } else if (currencyPosition == 'after_price_with_space') {
                appendCurrency = `0.00 ${currencyIcon}`
            }

            $("#monthly_payment").html(appendCurrency);
            $("#total_interest").html(appendCurrency);
            $("#total_payment").html(appendCurrency);

        })

    </script>
